 }
@@ -412,6 +415,15 @@ sub process_cfg_file {
 		}
 	}

+	if ($change_needed && $cfg_file eq "ip_allow.config" && $syncds_updates_ipallow != 1) {
+		if ($script_mode == $BADASS) {
+			$trafficserver_restart_needed++;
