 // representation.
 struct QInt8 {
-  QInt8() {}
+  QInt8() : value(0) {}
   QInt8(const int8_t v) : value(v) {}
   QInt8(const QInt32 v);

@@ -59,7 +59,7 @@ struct QInt8 {
 };

 struct QUInt8 {
-  QUInt8() {}
+  QUInt8() : value(0) {}
