+                   err = mp_mulmod(s, &b, curve->order, s);
                    if (err != MP_OKAY) break;

                    /* s = (e + xr)/k */
-                   err = mp_mulmod(s, &pubkey.k, curve->order, s);
+                   err = mp_mod(s, curve->order, s);
